  event RedeemDenied(uint256 marketId, address trader, bool isLong);

  /// @notice called by traders when market is denied before approval TODO
  /// ??? if the market is denied, this function is called and everything is redeemed 
  /// validator will need to call this on denial + isLong = true to redeem their collateral.
  function redeemDeniedMarket(
    uint256 marketId, 
    bool isLong
  ) external _lock_ {
    require(!restriction_data[marketId].alive, "Market Still During Assessment"); // TODO
    require(restriction_data[marketId].duringAssessment, "Market has been approved");
    SyntheticZCBPool bondPool = markets[marketId].bondPool; 

    uint256 collateral_amount;
    uint256 balance; 
    // Get collateral at stake in shorts, which will be directly given back to traders
    if(!isLong){
      balance = markets[marketId].shortZCB.balanceOf(msg.sender); 
      require(balance >= 0, "Empty");

      // TODO this means if trader's loss will be refunded if loss was realized before denied market
      collateral_amount = shortTrades[marketId][msg.sender]; 
      delete shortTrades[marketId][msg.sender]; 
      emit RedeemDenied(marketId, msg.sender, false);

      //Burn all their balance
      bondPool.trustedBurn(msg.sender, balance, false);
    } 

    // Get collateral at stake in longs, which will be directly given back to traders
    else {
      balance = markets[marketId].longZCB.balanceOf(msg.sender); 
      require(balance >= 0, "Empty");

      // TODO this means if trader's loss will be refunded if loss was realized before denied market
      if (controller.isValidator(marketId, msg.sender) && controller.hasApproved(marketId, msg.sender)) {
        collateral_amount = controller.deniedValidator(marketId, msg.sender);
      }
      else{
        collateral_amount = longTrades[marketId][msg.sender]; 
        delete longTrades[marketId][msg.sender]; 
        emit RedeemDenied(marketId, msg.sender, true);
      }

      // Burn all their balance 
      bondPool.trustedBurn(msg.sender, balance, true); 

      // This means that the sender is a manager
      if (queuedRepUpdates[msg.sender] > 0){
        unchecked{queuedRepUpdates[msg.sender] -= 1;} 
      }    
    }

    // Before redeem_transfer is called all funds for this instrument should be back in the vault
    controller.redeem_transfer(collateral_amount, msg.sender, marketId);
    //TODO need to check if last redeemer, so can kill market.
  }


  /// @notice trader will redeem entire balance of ZCB
  /// Needs to be called at maturity, market needs to be resolved first(from controller)
  function redeem(
    uint256 marketId
    ) external _lock_ returns(uint256 collateral_redeem_amount){
    require(!restriction_data[marketId].alive, "!Active"); 
    require(restriction_data[marketId].resolved, "!resolved"); 
    require(!redeemed[marketId][msg.sender], "Redeemed");
    redeemed[marketId][msg.sender] = true; 

    SyntheticZCBPool bondPool = markets[marketId].bondPool; 

    if (controller.isValidator(marketId, msg.sender)) controller.redeemValidator(marketId, msg.sender);

    uint256 zcb_redeem_amount = markets[marketId].longZCB.balanceOf(msg.sender); 
    uint256 redemption_price = redemption_prices[marketId]; 
    collateral_redeem_amount = redemption_price.mulWadDown(zcb_redeem_amount); 

    if (!controller.isValidator(marketId, msg.sender)) { // TODO should validators get reputation if they do ok.
      reputationManager.recordPush(msg.sender, marketId, redemption_price, false, zcb_redeem_amount); 
    }

    // This means that the sender is a manager
    if (queuedRepUpdates[msg.sender] > 0){
     unchecked{queuedRepUpdates[msg.sender] -= 1;} 
   }

    bondPool.trustedBurn(msg.sender, zcb_redeem_amount, true); 
    controller.redeem_transfer(collateral_redeem_amount, msg.sender, marketId);
  }

  /// @notice called by short buyers when market is resolved  
  function redeemShortZCB(
    uint256 marketId 
    ) external _lock_ returns(uint256 collateral_redeem_amount){
    require(!restriction_data[marketId].alive, "Active"); 
    require(restriction_data[marketId].resolved, "!resolved"); 
    require(!redeemed[marketId][msg.sender], "Redeemed");
    redeemed[marketId][msg.sender] = true; 

    SyntheticZCBPool bondPool = markets[marketId].bondPool; 

    uint256 shortZCB_redeem_amount = markets[marketId].shortZCB.balanceOf(msg.sender); 
    uint256 long_redemption_price = redemption_prices[marketId];
    uint256 redemption_price = long_redemption_price >= config.WAD 
                               ? 0 
                               : config.WAD - long_redemption_price; 
    collateral_redeem_amount = redemption_price.mulWadDown(shortZCB_redeem_amount);

    bondPool.trustedBurn(msg.sender, shortZCB_redeem_amount, false); 
    controller.redeem_transfer(collateral_redeem_amount, msg.sender, marketId); 
  }

  /// @notice returns the manager's maximum leverage 
  function getMaxLeverage(address manager) public view returns(uint256){
    //return (repToken.getReputationScore(manager) * config.WAD).sqrt(); //TODO experiment 
    return (controller.getTraderScore(manager) * config.WAD).sqrt();
  }

  mapping(uint256=>mapping(address=> LeveredBond)) public leveragePosition; 
  struct LeveredBond{
    uint128 debt; //how much collateral borrowed from vault 
    uint128 amount; // how much bonds were bought with the given leverage
  }

  /// @notice for managers that are a) meet certain reputation threshold and b) choose to be more
  /// capital efficient with their zcb purchase. 
  /// @param _amountIn (in collateral) already accounts for the leverage, so the actual amount manager is transferring
  /// is _amountIn/_leverage 
  /// @dev the marketmanager should take custody of the quantity bought with leverage
  /// and instead return notes of the levered position 
  /// TODO do + instead of creating new positions and implied prob cumulative 
  function buyBondLevered(
    uint256 _marketId, 
    uint256 _amountIn, 
    uint256 _priceLimit, 
    uint256 _leverage //in 18 dec 
    ) external _lock_ returns(uint256 amountIn, uint256 amountOut){
    require(restriction_data[_marketId].duringAssessment, "PhaseERR"); 
    require(!restriction_data[_marketId].resolved, "!resolved");
    require(_leverage <= getMaxLeverage(msg.sender) && _leverage >= config.WAD, "!leverage");
    _canBuy(msg.sender, int256(_amountIn), _marketId);
    SyntheticZCBPool bondPool = markets[_marketId].bondPool; 

    // stack collateral from trader and borrowing from vault 
    uint256 amountPulled = _amountIn.divWadDown(_leverage); 
    bondPool.BaseToken().transferFrom(msg.sender, address(this), amountPulled); 
    controller.pullLeverage(_marketId, _amountIn - amountPulled); 

    // Buy with leverage, zcb transferred here
    bondPool.BaseToken().approve(address(this), _amountIn); 
    (amountIn, amountOut) = bondPool.takerOpen(true, int256(_amountIn), _priceLimit, abi.encode(address(this))); 

    //Need to log assessment trades for updating reputation scores or returning collateral when market denied 
    _logTrades(_marketId, msg.sender, _amountIn, 0, true, true);

    // Get implied probability estimates by summing up all this managers bought for this market 
    // assessment_probs[_marketId][msg.sender] = controller.calcImpliedProbability(
    //     amountOut, 
    //     amountIn, 
    //     getTraderBudget(_marketId, msg.sender) 
    // ); 

    // Phase Transitions when conditions met
    if(restriction_data[_marketId].onlyReputable){
      uint256 total_bought = loggedCollaterals[_marketId];

      if (total_bought >= parameters[_marketId].omega.mulWadDown(
            controller
            .getVault(_marketId)
            .fetchInstrumentData(_marketId)
            .principal)
      ) {
        restriction_data[_marketId].onlyReputable = false;
        emit MarketPhaseSet(_marketId, restriction_data[_marketId]);
      }
    }
    // create note to trader 
    leveragePosition[_marketId][msg.sender] = LeveredBond(uint128(_amountIn - amountPulled ),uint128(amountOut)) ; 
  }

  function redeemLeveredBond(uint256 marketId) public{
    require(!restriction_data[marketId].alive, "!Active"); 
    require(restriction_data[marketId].resolved, "!resolved"); 
    require(!redeemed[marketId][msg.sender], "Redeemed");
    redeemed[marketId][msg.sender] = true; 

    if (controller.isValidator(marketId, msg.sender)) controller.redeemValidator(marketId, msg.sender); 

    LeveredBond memory position = leveragePosition[marketId][msg.sender]; 
    require(position.amount>0, "ERR"); 

    uint256 redemption_price = redemption_prices[marketId]; 
    uint256 collateral_back = redemption_price.mulWadDown(position.amount) ; 
    uint256 collateral_redeem_amount = collateral_back >= uint256(position.debt)  
        ? collateral_back - uint256(position.debt) : 0; 

    if (!controller.isValidator(marketId, msg.sender)) {
      // bool increment = redemption_price >= config.WAD? true: false;
      // controller.updateReputation(marketId, msg.sender, increment);
      // reputationManager.recordPush(msg.sender, marketId, redemption_price, false, zcb_redeem_amount); 

    }

    // This means that the sender is a manager
    if (queuedRepUpdates[msg.sender] > 0){
     unchecked{queuedRepUpdates[msg.sender] -= 1;} 
    }

    leveragePosition[marketId][msg.sender].amount = 0; 
    markets[marketId].bondPool.trustedBurn(address(this), position.amount, true); 
    controller.redeem_transfer(collateral_redeem_amount, msg.sender, marketId);
  }

  function redeemDeniedLeveredBond(uint256 marketId) public returns(uint collateral_amount){
    LeveredBond memory position = leveragePosition[marketId][msg.sender]; 
    require(position.amount>0, "ERR"); 
    leveragePosition[marketId][msg.sender].amount = 0; 

    // TODO this means if trader's loss will be refunded if loss was realized before denied market
    if (controller.isValidator(marketId, msg.sender)) {
      collateral_amount = controller.deniedValidator(marketId, msg.sender);
    }
    else{
      collateral_amount = longTrades[marketId][msg.sender]; 
      delete longTrades[marketId][msg.sender]; 
    }

    // Burn all their position, 
    markets[marketId].bondPool.trustedBurn(address(this), position.amount, true); 

    // This means that the sender is a manager
    if (queuedRepUpdates[msg.sender] > 0){
      unchecked{queuedRepUpdates[msg.sender] -= 1;} 
    }    

    // Before redeem_transfer is called all funds for this instrument should be back in the vault
    controller.redeem_transfer(collateral_amount - uint256(position.debt), msg.sender, marketId);
  }

  function min(uint256 a, uint256 b) internal pure returns (uint256) {
        return a <= b ? a : b;
    }
}

","keccak256":"0x3daf8790a4e76b0418a5848141dd625494365e143eef039220a69e9aedaaf482"},"contracts/protocol/reputationmanager.sol":{"content":"pragma solidity ^0.8.16;
import "lib/forge-std/src/console.sol";

import {config} from "../utils/helpers.sol"; 
import {FixedPointMathLib} from "lib/solmate/src/utils/FixedPointMathLib.sol";
import {Controller} from "./controller.sol"; 

contract ReputationManager {
    using FixedPointMathLib for uint256;

    mapping(address=>uint256) public trader_scores; // trader address => score
    mapping(address=>bool) public isRated;
    address[] public traders;

    address controller;
    address marketManager;

    address deployer;

    modifier onlyProtocol() {
        require(msg.sender == controller || msg.sender == marketManager || msg.sender == deployer, "ReputationManager: !protocol");
        _;
    }
    // event pullLogged(uint256 marketId, address recipient, );
    event reputationUpdated(uint256 marketId, address recipient);

    constructor(
        address _controller,
        address _marketManager
    ) {
       controller = _controller;
       marketManager = _marketManager;
       deployer = msg.sender;
    }

    struct RepLog{
        uint256 collateralAmount; 
        uint256 bondAmount; 
        uint256 budget; 
        bool perpetual; 
        //TODO place this info elsewhere or do packing
    }

    mapping(address=> mapping(uint256=> RepLog)) public repLogs; //trader=> marketid=>replog 

    function getRepLog(address trader, uint256 marketId) external view returns(RepLog memory){
        return repLogs[trader][marketId]; 
    }

    /// @notice record for reputation updates whenever trader buys longZCB
    function recordPull(
        address trader, 
        uint256 marketId, 
        uint256 bondAmount, 
        uint256 collateral_amount, 
        uint256 budget,
        bool perpetual
        ) external onlyProtocol{
        RepLog memory newLog = repLogs[trader][marketId]; 
        newLog.collateralAmount += collateral_amount; 
        newLog.bondAmount += bondAmount; 
        newLog.perpetual = perpetual; 
        newLog.budget = budget; 

        repLogs[trader][marketId] = newLog;   
    }

    /// @notice updates reputation whenever trader redeems 
    /// param premature is true if trader redeems/sells before maturity 
    /// param redeemAmount is 0 if !perpetual, since for those traders will redeem all at once 
    function recordPush(
        address trader, 
        uint256 marketId, 
        uint256 bondPrice, 
        bool premature, 
        uint256 redeemAmount//in bonds 
        ) external onlyProtocol {
        RepLog memory newLog = repLogs[trader][marketId]; 

        uint256 avgPrice = newLog.collateralAmount.divWadDown(newLog.bondAmount); 

        // Penalize premature sells 
        if(premature){
            _updateReputation(marketId, trader, false, 1e18, newLog); 
            delete repLogs[trader][marketId]; 
        }
        // What about when trader buys during assessment at discount?  
        // if instrument is perpetual, increment if exit price > avgPrice by 
        // proportional to the diff in price  
        if(newLog.perpetual){
            if (bondPrice >= avgPrice) 
                _updateReputation(marketId, trader, true,  bondPrice-avgPrice, newLog); 
            else 
                _updateReputation(marketId, trader, false,  avgPrice - bondPrice, newLog); 

            require(newLog.collateralAmount >= redeemAmount.mulWadDown(avgPrice), "logERR1");
            require(newLog.bondAmount>= redeemAmount, "logERR2"); 
            unchecked{
                newLog.collateralAmount -= redeemAmount.mulWadDown(avgPrice);
                newLog.bondAmount -= redeemAmount; 
                repLogs[trader][marketId] = newLog; 
            }
        }

        // if instrument is fixed term, increment if redemptionprice >= 1, 
        // no change if 1>= redemption price >= avgPrice and decrement otherwise 
        else{
            //bondPrice is redemptionprice for fixed term instruments
            if(bondPrice>= 1e18)
                _updateReputation(marketId, trader, true, bondPrice-avgPrice, newLog); 

            else if(bondPrice <= avgPrice && bondPrice < 1e18)
                _updateReputation(marketId, trader, false, avgPrice-bondPrice, newLog);  
            // Redeeming everything at maturity 
            delete repLogs[trader][marketId];             
        }


    }

    /// @notice given a hypoethetical bond price, calculate how much reputation one will gain
    function expectedRepGain(
        address trader, 
        uint256 marketId, 
        uint256 bondPrice, 
        uint256 incremntalAmount, 
        uint256 incrementalBondAmount) external view returns(int256 expectedGain){
        RepLog memory log = repLogs[trader][marketId]; 

        uint256 avgPrice = (log.collateralAmount+incremntalAmount)
            .divWadDown(log.bondAmount + incrementalBondAmount); 
        uint256 priceChange = bondPrice- avgPrice; 
        uint256 implied_probs; 

        if(log.perpetual){
            implied_probs = log.collateralAmount.divWadDown(log.budget); 
            expectedGain = (bondPrice >= avgPrice)
                ? int256(priceChange.mulWadDown(implied_probs))
                : -int256(priceChange.mulWadDown(implied_probs)); 
        } else {
            implied_probs = calcImpliedProbability(
                log.bondAmount, 
                log.collateralAmount, 
                log.budget
                ); 
            if(bondPrice>= 1e18)
                expectedGain = int256(priceChange.mulWadDown(implied_probs)); 
            else if(bondPrice <= avgPrice && bondPrice < 1e18)
                expectedGain = -int256(priceChange.mulWadDown(implied_probs)); 
        }
    }

    /// @notice when market is resolved(maturity/early default), calculates score
    /// and update each assessment phase trader's reputation, called by individual traders when redeeming
    function _updateReputation(
        uint256 marketId,
        address trader,
        bool increment, 
        uint256 priceChange, 
        RepLog memory log
    ) internal {

        uint256 implied_probs = log.perpetual
            ? log.collateralAmount.divWadDown(log.budget)
            : calcImpliedProbability(
                log.bondAmount, 
                log.collateralAmount, 
                log.budget
                ); 

        uint256 change = priceChange.mulWadDown(implied_probs); 
                        // log.perpetual
                        // ? priceChange.mulWadDown(implied_probs) * 
                        // : implied_probs.mulDivDown(implied_probs, config.WAD);

        if (increment) {
            incrementScore(trader, change);
        } else {
            decrementScore(trader, change);
        }
    }

    //function expectedRepGain()


    /// @notice calculates implied probability of the trader, used to
    /// update the reputation score by brier scoring mechanism
    /// @param budget of trader in collateral decimals
    function calcImpliedProbability(
        uint256 bondAmount,
        uint256 collateral_amount,
        uint256 budget
    ) public pure returns (uint256) {
        require(bondAmount > 0 && collateral_amount > 0, "0div"); 
      // TODO underflows when avgprice bigger than wad
        uint256 avg_price = collateral_amount.divWadDown(bondAmount);
        uint256 b = avg_price.mulWadDown(config.WAD - avg_price);
        uint256 ratio = bondAmount.divWadDown(budget);

        return ratio.mulWadDown(b) + avg_price;
    }

    function calculateMinScore(uint256 percentile) view external returns (uint256) {
        uint256 l = traders.length * config.WAD;
        if (percentile / 1e2 == 0) {
        return 0;
        }
        uint256 x = l.mulWadDown(percentile / 1e2);
        x /= config.WAD;
        return trader_scores[traders[x - 1]];
    }

    // change visiblity, external only for testing.
    function setTraderScore(address trader, uint256 score) external {
        uint256 prev_score = trader_scores[trader];
        if (score > prev_score) {
        incrementScore(trader, score - prev_score);
        } else if (score < prev_score) {
        decrementScore(trader, prev_score - score);
        }
    }

    function isReputable(address trader, uint256 percentile) view external returns (bool) {
        uint256 k = findTrader(trader);
        uint256 n = (traders.length - (k+1))*config.WAD;
        uint256 N = traders.length*config.WAD;
        uint256 p = uint256(n).divWadDown(N)*10**2;

        if (p >= percentile) {
        return true;
        } else {
        return false;
        }
    }

    // TODO 
    function expectedIncrement(address manager) public view returns(uint256){

    }

    /**
    @dev percentile is is wad 0-100
    @notice returns a list of top X percentile traders excluding the utilizer. 
    */
    function filterTraders(uint256 percentile, address utilizer) view public returns (address[] memory) {
        uint256 l = traders.length * config.WAD;

        // if below minimum percentile, return all traders excluding the utilizer
        if (percentile / 1e2 == 0) {
        if (isRated[utilizer]) {
            address[] memory result = new address[](traders.length - 1);

            uint256 j = 0;
            for (uint256 i=0; i<traders.length; i++) {
            if (utilizer == traders[i]) {
                j = 1;
                continue;
            }
            result[i - j] = traders[i];
            }
            return result;
        } else {
            return traders;
        }
        }

        uint256 x = l.mulWadDown((config.WAD*100 - percentile) / 1e2);
        x /= config.WAD;

        address[] memory selected; 
        if (utilizer == address(0) || !isRated[utilizer]) {
        selected = new address[](x);
        for (uint256 i=0; i<x; i++) {
            selected[i] = traders[i];
        }
        } else {
        selected = new address[](x - 1);
        uint256 j=0;
        for (uint256 i = 0; i<x; i++) {
            if (traders[i] == utilizer) {
            j = 1;
            continue;
            }
            selected[i - j] = traders[i];
        }
        }

        return selected;
    }

    function getTraders() view public returns (address[] memory) {
        return traders;
    }

    event ScoreUpdated(address trader, uint256 score);
    /**
    @notice increments trader's score
    @dev score >= 0, update > 0
    */
    function incrementScore(address trader, uint256 update) onlyProtocol public {
        trader_scores[trader] += update;
        _updateRanking(trader, true);
        emit ScoreUpdated(trader, trader_scores[trader]);
    }

    function testIncrementScore(uint256 update) public {
        trader_scores[msg.sender] += update;
        _updateRanking(msg.sender, true);
        emit ScoreUpdated(msg.sender, trader_scores[msg.sender]);
    }

    /**
    @notice decrements trader's score
    @dev score >= 0, update > 0
    */
    function decrementScore(address trader, uint256 update) onlyProtocol public {
        if (update >= trader_scores[trader]) {
        trader_scores[trader] = 0;
        } else {
        trader_scores[trader] -= update;
        }
        _updateRanking(trader, false);
        emit ScoreUpdated(trader, trader_scores[trader]);
    }

    /**
    @notice updates top trader array
    @dev holy moly is this ugly
    */
    function _updateRanking(address trader, bool increase) internal {
        uint256 score = trader_scores[trader];

        if (!isRated[trader]) {
        isRated[trader] = true;
        if (traders.length == 0) {
            traders.push(trader);
            return;
        }
        for (uint256 i=0; i<traders.length; i++) {
            if (score > trader_scores[traders[i]]) {
            traders.push(address(0));
            _shiftRight(i, traders.length-1);
            traders[i] = trader;
            return;
            }
            if (i == traders.length - 1) {
            traders.push(trader);
            return;
            }
        }
        } else {
        uint256 k = findTrader(trader);
        //swap places with someone.
        if ((k == 0 && increase)
        || (k == traders.length - 1 && !increase)) {
            return;
        }

        if (increase) {
            for (uint256 i=0; i<k; i++) {
            if (score > trader_scores[traders[i]]) {
                _shiftRight(i,k);
                traders[i] = trader;
                return;
            }
            }
        } else {
            for (uint256 i=traders.length - 1; i>k; i--) {
            if (score < trader_scores[traders[i]]) {
                _shiftLeft(k, i);
                traders[i] = trader;
                return;
            }
            }
        }
        }
    }

    function findTrader(address trader) public view returns (uint256) {
    for (uint256 i=0; i<traders.length; i++) {
        if (trader == traders[i]) {
            return i;
        }
        }
    }

    /**
    @notice helpers
    */
    function _shiftRight(uint256 pos, uint256 end) internal {
        for (uint256 i=end; i>pos; i--) {
        traders[i] = traders[i-1];
        }
    }

    function _shiftLeft(uint256 pos, uint256 end) internal {
        for (uint256 i=pos; i<end; i++) {
        traders[i] = traders[i+1];
        }
    }

}","keccak256":"0xaa173296471c8500d78eea8adb6640c8645f56e9bae86ef791b2e37181acb4b1"},"contracts/protocol/reputationtoken.sol":{"content":"pragma solidity ^0.8.16; 
//https://github.com/poap-xyz/poap-contracts/tree/master/contracts
import {ERC721} from "lib/solmate/src/tokens/ERC721.sol";
import {Controller} from "./controller.sol";
import "lib/forge-std/src/console.sol";



contract ReputationNFT is ERC721 {
  mapping(uint256 => ReputationData) internal _reputation; // id to reputation
  mapping(address => uint256) internal _ownerToId;
  mapping(uint256 => TraderData[]) internal _marketData; // **MarketId to Market's data needed for calculating brier score.

  uint256 private nonce = 1;
  Controller controller;
  uint256 SCALE = 1e18;


  struct ReputationData {
    uint256 n; // number of markets participated in => regular uint256
    uint256 score; 
  }

  struct TraderData { // for each market
    address trader;
    uint256 tokensBought;
  }

  struct TopReputation{
    address trader; 
    uint256 score; 
  }

  uint256 private constant topRep = 100; 
  TopReputation[topRep] topReputations; 

  mapping(uint256=>mapping(address=>bool)) canTrade; //marketID-> address-> cantrade
  mapping(uint256=>bool) allowAll; 
  mapping(address=>bool) isUnique; 
  address[] unique_traders; 
  mapping(uint256=>mapping(address=>uint256)) public balances; // marketId => market manager address => how much collateral already bought.

  modifier onlyController() {
    require(msg.sender == address(controller));
    _;
  }

  constructor (
    address _controller
  ) ERC721("Debita Reputation Token", "DRT") {
    controller = Controller(_controller);
  }

  /**
   @notice incrementBalance
   */
  function incrementBalance(uint256 marketId, address trader, uint256 amount) external onlyController {
    balances[marketId][trader] += amount;
  }

  /**
   @notice called post reputation update
   */
  function removeBalance(uint256 marketId, address trader) external onlyController {
    delete balances[marketId][trader];
  }

  function _baseURI() internal pure returns (string memory baseURI) {
    baseURI = "";
  }

  function tokenURI(uint256 id) public view override returns (string memory) {
    require(_ownerOf[id] != address(0), "Invalid Identifier");

    string memory baseURI = _baseURI();
    return bytes(baseURI).length > 0 ? string(abi.encodePacked(baseURI, id)) : "";
  }

  function mint(address to) external {
    require(_ownerToId[to] == uint256(0), "can only mint one reputation token");
    super._mint(to, nonce);
    _ownerToId[to] = nonce;

    // Set default score, if this goes to 0 cannot trade
    _reputation[_ownerToId[to]].score = 1e18; 

    nonce++;
  }

  function getReputationScore(address owner) view external returns (uint256){
    require(_ownerToId[owner] != uint256(0), "No Id found");
    return _reputation[_ownerToId[owner]].score;
  }

  function setReputationScore(address owner, uint256 score) external returns (uint256) 
  //onlyOwner
  {
    require(_ownerToId[owner] != uint256(0), "No Id found");
    return _reputation[_ownerToId[owner]].score = score;
  }


  function updateScore(address to, int256 score) external onlyController{
    require(_ownerToId[to] != uint256(0), "No Id found");

    ReputationData storage data = _reputation[_ownerToId[to]];
    if (score > 0) data.score = data.score + uint256(score);
    else{
        if (data.score <= uint256(-score)) data.score = 0; 
        else data.score = data.score - uint256(-score);
      } 

    storeTopX(data.score, to); 
  }


  function addScore(address to, uint256 score) external onlyController
   {
    require(_ownerToId[to] != uint256(0), "No Id found");

    ReputationData storage data = _reputation[_ownerToId[to]];
    data.score = data.score + score; 

    storeTopX(data.score, to); 
  }

  function decrementScore(address to, uint256 score) external onlyController
   {
    require(_ownerToId[to] != uint256(0), "No Id found");

    ReputationData storage data = _reputation[_ownerToId[to]];
    if (data.score <= score) data.score = 0; 
    else data.score = data.score - score; 

    storeTopX(data.score, to); 
  }

  function addAverageScore(address to, uint256 score) external onlyController

   {
    require(_ownerToId[to] != uint256(0), "No Id found");

    ReputationData storage data = _reputation[_ownerToId[to]];

    if (data.n == 0) {
      data.score = score;
    } else {
      data.score = (data.score / data.n + score) / (data.n + 1);
    }

    data.n++;
  }

  /**
   @notice reset scores
   */
  function resetScore(address to) external {
    require(_ownerToId[to] != uint256(0), "No Id found");
    delete _reputation[_ownerToId[to]];
  }

  /// @notice called by controller when initiating market,
  function storeTopReputation(uint256 topX, uint256 marketId) external onlyController{
    if (getAvailableTopX() < topX) {
      allowAll[marketId] =true; 
      return; 
    }

    for (uint256 i; i<topX; i++){
      canTrade[marketId][topReputations[i].trader] = true;
    }

  }

  /// @notice gets the x's ranked score from all reputation scores 
  /// @dev returns 0 if topX is greater then avaiable nonzero rep scores-> everyone is allowed
  /// during reputation constraint periods 
  function getMinRepScore(uint256 topX) public view returns(uint256) {
    if (getAvailableTopX() < topX) {
      return 0; 
    }
    return topReputations[topX].score;
  }

  function getAvailableTopX() public view returns(uint256){
    return unique_traders.length; 
  }

  function getAvailableTraderNum() public view returns(uint256){
    return nonce -1; 
  }

  /// @notice whether trader is above reputation threshold 
  function traderCanTrade(uint256 marketId, address trader) external returns(bool){
    return allowAll[marketId]? true : canTrade[marketId][trader]; 
  }

  /// @notice called whenever a score is incremented   
  function storeTopX(uint256 score, address trader) internal {
    uint256 i = 0;

    for(i; i < topReputations.length; i++) {
      if(topReputations[i].score < score) {
        break;
      }
    }
    // shifting the array of position (getting rid of the last element) 
    for(uint j = topReputations.length - 1; j > i; j--) {
        topReputations[j].score = topReputations[j - 1].score;
        topReputations[j].trader = topReputations[j - 1].trader;
    }
    // update the new max element 
    topReputations[i].score = score;
    topReputations[i].trader = trader;

    if (isUnique[trader]) return; 
    isUnique[trader] = true; 
    unique_traders.push(trader);

  }

  function testStore() public view {
    for (uint i=0; i<10; i++){
      console.log('score', topReputations[i].score); 
    }
  }  
}","keccak256":"0xecaf77b1bd3c0c68f9e4181314fb094cfce1d1f9445f432a9bf68c9138e860b1"},"contracts/protocol/validatorManager.sol":{"content":"pragma solidity ^0.8.16;

import {Controller} from "./controller.sol";
import {MarketManager} from "./marketmanager.sol";
import {ReputationManager} from "./reputationmanager.sol";
import {SyntheticZCBPool} from "../bonds/synthetic.sol";
import {config} from "../utils/helpers.sol";
import {ERC4626} from "../vaults/mixins/ERC4626.sol";
import {SafeTransferLib} from "lib/solmate/src/utils/SafeTransferLib.sol";
import {ERC20} from "lib/solmate/src/tokens/ERC20.sol";
import {FixedPointMathLib} from "lib/solmate/src/utils/FixedPointMathLib.sol";

contract ValidatorManager {
    using SafeTransferLib for ERC20;
    using FixedPointMathLib for uint256;
    Controller private controller;
    ReputationManager private reputationManager;
    MarketManager private marketManager;

    modifier onlyController () {
        require(msg.sender == address(controller), "not controller");
        _;
    }

     /*----Validator Logic----*/
    struct ValidatorData {
        mapping(address => uint256) sales; // amount of zcb bought per validator
        mapping(address => bool) staked; // true if address has staked vt (approved)
        mapping(address => bool) resolved; // true if address has voted to resolve the market
        address[] validators;
        uint256 val_cap; // total zcb validators can buy at a discount
        uint256 avg_price; //price the validators can buy zcb at a discount
        bool requested; // true if already requested random numbers from array.
        uint256 totalSales; // total amount of zcb bought;
        uint256 totalStaked; // total amount of vault token staked.
        uint256 numApproved;
        uint256 initialStake; // amount staked
        uint256 finalStake; // amount of stake recoverable post resolve
        uint256 numResolved; // number of validators calling resolve on early resolution.
    }

    mapping(uint256 => uint256) requestToMarketId;
    mapping(uint256 => ValidatorData) public validator_data;

    constructor (
        address _controller,
        address _marketManager,
        address _reputationManager
        ) {
        controller = Controller(_controller);
        reputationManager = ReputationManager(_reputationManager);
        marketManager = MarketManager(_marketManager);
    }

    function validatorSetup(
        uint256 marketId,
        uint256 principal,
        bool isPool
    ) external onlyController {
        require(principal != 0, "0 principal");
        _getValidators(marketId);
        _setValidatorCap(marketId, principal, isPool);
        _setValidatorStake(marketId, principal);
    }

    function _getValidators(uint256 marketId) public {
        // retrieve traders that meet requirement.
        // address instrument = market_data[marketId].instrument_address;
        (,address utilizer) = controller.market_data(marketId);

        (uint256 N, , , , , uint256 r, , ) = marketManager.parameters(marketId);
        address[] memory selected = reputationManager.filterTraders(
            r,
            utilizer
        );

        // if there are not enough traders, set validators to all selected traders.
        if (selected.length <= N) {
            validator_data[marketId].validators = selected;

            if (selected.length < N) {
                revert("not enough rated traders");
            }

            return;
        }

        validator_data[marketId].requested = true;

        uint256 _requestId = 1;
        // uint256 _requestId = COORDINATOR.requestRandomWords(
        //   keyHash,
        //   subscriptionId,
        //   requestConfirmations,
        //   callbackGasLimit,
        //   uint32(parameters[marketId].N)
        // );

        requestToMarketId[_requestId] = marketId;
    }

    function _setValidatorCap(
        uint256 marketId,
        uint256 principal,
        bool isPool //??
    ) internal {
        SyntheticZCBPool bondingPool = marketManager.getPool(marketId);
        (, uint256 sigma, , , , , , ) = marketManager.parameters(marketId);
        require(config.isInWad(sigma) && config.isInWad(principal), "paramERR");
        ValidatorData storage valdata = validator_data[marketId];

        uint256 valColCap = (sigma.mulWadDown(principal));

        // Get how much ZCB validators need to buy in total, which needs to be filled for the market to be approved
        uint256 discount_cap = bondingPool.discount_cap();
        uint256 avgPrice = valColCap.divWadDown(discount_cap);

        valdata.val_cap = discount_cap;
        valdata.avg_price = avgPrice;
    }

     /**
   @notice sets the amount of vt staked by a single validator for a specific market
   @dev steak should be between 1-0 wad.
   */
    function _setValidatorStake(uint256 marketId, uint256 principal) internal {
        //get vault
        uint256 vaultId = controller.id_parent(marketId);
        ERC4626 vault = controller.vaults(vaultId);
        // ERC4626 vault = ERC4626(vaults[id_parent[marketId]]);
        uint256 shares = vault.convertToShares(principal);
        (, , , , , , , uint256 steak) = marketManager.parameters(marketId);
        validator_data[marketId].initialStake = steak.mulWadDown(shares);
    }

    function deniedValidator(uint256 marketId, address validator)
        external
        onlyController
        returns (uint256 collateral_amount)
    {
        //??? is this correct
        collateral_amount = validator_data[marketId]
            .sales[validator]
            .mulWadDown(validator_data[marketId].avg_price);
        delete validator_data[marketId].sales[validator];
    }

    function redeemValidator(uint256 marketId, address validator)
        external
        onlyController
    {
        delete validator_data[marketId].sales[validator];
    }

    function getValidatorRequiredCollateral(uint256 marketId)
        public
        view
        returns (uint256)
    {
        uint256 val_cap = validator_data[marketId].val_cap;
        (uint256 N, , , , , , , ) = marketManager.parameters(marketId);
        uint256 zcb_for_sale = val_cap / N;
        return zcb_for_sale.mulWadDown(validator_data[marketId].avg_price);
    }

    function unlockValidatorStake(uint256 marketId, address validator) onlyController external {
        require(isValidator(marketId, validator), "!validator");
        require(validator_data[marketId].staked[validator], "!stake");
        (bool duringMarketAssessment, , , , , ) = marketManager
            .restriction_data(marketId);

        // market early denial, no loss.
        uint256 vaultId = controller.id_parent(marketId);
        ERC4626 vault = ERC4626(controller.vaults(vaultId));
        if (duringMarketAssessment) {
            ERC20(controller.getVaultAd(marketId)).safeTransfer(
                validator,
                validator_data[marketId].initialStake
            );
            validator_data[marketId].totalStaked -= validator_data[marketId]
                .initialStake;
        } else {
            // market resolved.
            ERC20(controller.getVaultAd(marketId)).safeTransfer(
                validator,
                validator_data[marketId].finalStake
            );
            validator_data[marketId].totalStaked -= validator_data[marketId]
                .finalStake;
        }

        validator_data[marketId].staked[validator] = false;
    }

     function updateValidatorStake(
        uint256 marketId,
        uint256 principal,
        uint256 principal_loss
    ) public onlyController {
        if (principal_loss == 0) {
            validator_data[marketId].finalStake = validator_data[marketId]
                .initialStake;
            return;
        }

        uint256 vaultId = controller.id_parent(marketId);
        ERC4626 vault = ERC4626(controller.vaults(vaultId));
        uint256 p_shares = vault.convertToShares(principal);
        uint256 p_loss_shares = vault.convertToShares(principal_loss);

        uint256 totalStaked = validator_data[marketId].totalStaked;
        uint256 newTotal = totalStaked /
            2 +
            (p_shares - p_loss_shares).divWadDown(p_shares).mulWadDown(
                totalStaked / 2
            );

        ERC4626(controller.getVaultAd(marketId)).burn(totalStaked - newTotal);
        validator_data[marketId].totalStaked = newTotal;

        validator_data[marketId].finalStake =
            newTotal /
            validator_data[marketId].validators.length;
    }

    function validatorResolve(uint256 marketId, address validator) onlyController external {
        require(isValidator(marketId, validator), "!val");
        require(!validator_data[marketId].resolved[validator], "voted");

        validator_data[marketId].resolved[validator] = true;
        validator_data[marketId].numResolved++;
    }

    function isValidator(uint256 marketId, address user)
        public
        view
        returns (bool)
    {
        address[] storage _validators = validator_data[marketId].validators;
        for (uint256 i = 0; i < _validators.length; i++) {
            if (_validators[i] == user) {
                return true;
            }
        }
        return false;
    }

    function validatorApprove(uint256 marketId, address validator) external returns (uint256 collateral_required, uint256 zcb_for_sale) {
        require(isValidator(marketId, validator), "!Val");
        require(controller.marketCondition(marketId), "!condition");

        ValidatorData storage valdata = validator_data[marketId];
        require(!valdata.staked[validator], "!staked");

        // staking logic, TODO optional since will throw error on transfer.
        // require(ERC20(getVaultAd(marketId)).balanceOf(validator) >= valdata.initialStake, "not enough tokens to stake");

        // staked vault tokens go to controller
        ERC20(controller.getVaultAd(marketId)).safeTransferFrom(
            validator,
            address(this),
            valdata.initialStake
        );

        valdata.totalStaked += valdata.initialStake;
        valdata.staked[validator] = true;

        (uint256 N, , , , , , , ) = marketManager.parameters(marketId);
        zcb_for_sale = valdata.val_cap / N;
        collateral_required = zcb_for_sale.mulWadDown(
            valdata.avg_price
        );

        require(valdata.sales[validator] <= zcb_for_sale, "approved");

        valdata.sales[validator] += zcb_for_sale;
        valdata.totalSales += (zcb_for_sale + 1); //since division rounds down ??
        valdata.numApproved += 1;

        // marketManager actions on validatorApprove, transfers collateral to marketManager.
        // marketManager.validatorApprove(
        //     marketId,
        //     collateral_required,
        //     zcb_for_sale,
        //     validator
        // );

        // Last validator pays more gas, is fair because earlier validators are more uncertain
        if (controller.approvalCondition(marketId)) {
            controller.approveMarket(marketId);
            // marketManager.approveMarket(marketId); // For market to go to a post assessment stage there always needs to be a lower bound set
        }
    }

    function approvalCondition(uint256 marketId) public view returns (bool) {
        return (validator_data[marketId].totalSales >=
            validator_data[marketId].val_cap &&
            validator_data[marketId].validators.length ==
            validator_data[marketId].numApproved);
    }


    function getValidatorPrice(uint256 marketId) public view returns (uint256) {
        return validator_data[marketId].avg_price;
    }

    function getValidatorCap(uint256 marketId) public view returns (uint256) {
        return validator_data[marketId].val_cap;
    }

    function viewValidators(uint256 marketId)
        public
        view
        returns (address[] memory)
    {
        return validator_data[marketId].validators;
    }

    function fulfillRandomWords(
        uint256 requestId,
        uint256[] memory randomWords //internal
    ) external onlyController
    {
        uint256 marketId = requestToMarketId[requestId];
        (uint256 N, , , , , uint256 r, , ) = marketManager.parameters(marketId);

        assert(randomWords.length == N);

        // address instrument = market_data[marketId].instrument_address;
        (,address utilizer) = controller.market_data(marketId);

        address[] memory temp = reputationManager.filterTraders(r, utilizer);
        uint256 length = temp.length;

        // get validators
        for (uint8 i = 0; i < N; i++) {
            uint256 j = _weightedRetrieve(temp, length, randomWords[i]);
            validator_data[marketId].validators.push(temp[j]);
            temp[j] = temp[length - 1];
            length--;
        }
    }

    function _weightedRetrieve(
        address[] memory group,
        uint256 length,
        uint256 randomWord
    ) internal view returns (uint256) {
        uint256 sum_weights;

        for (uint8 i = 0; i < length; i++) {
            sum_weights += controller.getTraderScore(group[i]); //repToken.getReputationScore(group[i]);
        }

        uint256 tmp = randomWord % sum_weights;

        for (uint8 i = 0; i < length; i++) {
            uint256 wt = controller.getTraderScore(group[i]);
            if (tmp < wt) {
                return i;
            }
            unchecked {
                tmp -= wt;
            }
        }
    }

    function resolveCondition(uint256 marketId) public view returns (bool) {
        return (validator_data[marketId].numResolved ==
            validator_data[marketId].validators.length);
    }

    function hasApproved(uint256 marketId, address validator)
        public
        view
        returns (bool)
    {
        return validator_data[marketId].staked[validator];
    }

    function getNumApproved(uint256 marketId) public view returns (uint256) {
        //return validatorManager.getNumApproved(marketId);
        return validator_data[marketId].numApproved;
    }

    function getNumResolved(uint256 marketId) public view returns (uint256) {
        //return validatorManager.getNumResolved(marketId);
        return validator_data[marketId].numResolved;
    }

    function getTotalStaked(uint256 marketId) public view returns (uint256) {
        // return validatorManager.getTotalStaked(marketId);
        return validator_data[marketId].totalStaked;
    }

    function getTotalValidatorSales(uint256 marketId)
        public
        view
        returns (uint256)
    {
        // return validatorManager.getTotalValidatorSales(marketId);
        return validator_data[marketId].totalSales;
    }

    function getInitialStake(uint256 marketId) public view returns (uint256) {
        //return validatorManager.getInitialStake(marketId);
        return validator_data[marketId].initialStake;
    }

    function getFinalStake(uint256 marketId) public view returns (uint256) {
        //return validatorManager.getFinalStake(marketId);
        return validator_data[marketId].finalStake;
    }
}","keccak256":"0x5fe30a0b1279ede8fd4236ab83d726ffec1183790928dd74d1dcde69416dcb09"},"contracts/utils/helpers.sol":{"content":"pragma solidity ^0.8.4;


library config{

  uint256 public constant WAD_PRECISION = 18; 
  uint256 public constant WAD = 1e18; 
  uint256 public constant USDC_dec = 1e6; 
  uint256 public constant roundLimit = 1e14; //0.0001 

  //Max amount in one transaction 
  uint256 private constant max_amount = 1e8 * WAD; 

  //Min amount in one transaction 
  uint256 private constant min_amount = WAD/1e4; 

  function convertToWad(uint256 number, uint256 dec) internal pure returns(uint256 new_number){
    //number should not be 18 dec, but in collateral_dec
    new_number = number * (10 ** (WAD_PRECISION - dec));
    assert(new_number <= max_amount); 
  }

  function wadToDec(uint256 number, uint256 dec) internal pure returns(uint256 new_number){
    // number should be 18 dec 
    assert(isInWad(number)); 
    new_number = number/(10 ** (WAD_PRECISION - dec)); 

  }

  function isInWad(uint256 number) internal pure returns(bool){
    return (number >= min_amount); 
  }




}","keccak256":"0xd30e0b9fdc4387086c91449032c63c8190e50bd580306e1e2a0b0dccbf735779"},"contracts/vaults/auth/Auth.sol":{"content":"// SPDX-License-Identifier: AGPL-3.0-only
pragma solidity >=0.8.0;

/// @notice Provides a flexible and updatable auth pattern which is completely separate from application logic.
/// @author Solmate (https://github.com/transmissions11/solmate/blob/main/src/auth/Auth.sol)
/// @author Modified from Dappsys (https://github.com/dapphub/ds-auth/blob/master/src/auth.sol)
abstract contract Auth {
    event OwnerUpdated(address indexed user, address indexed newOwner);

    event AuthorityUpdated(address indexed user, Authority indexed newAuthority);

    address public owner;

    Authority public authority;
    mapping(address=>bool) Authorized; 

    constructor(address _owner) {
        owner = _owner;

        emit OwnerUpdated(msg.sender, _owner);
    }

    modifier requiresAuth() virtual {
        require(isAuthorized(msg.sender, msg.sig), "UNAUTHORIZED");

        _;
    }

    function isAuthorized(address user, bytes4 functionSig) internal view virtual returns (bool) {
        //Authority auth = authority; // Memoizing authority saves us a warm SLOAD, around 100 gas.

        // Checking if the caller is the owner only after calling the authority saves gas in most cases, but be
        // aware that this makes protected functions uncallable even to the owner if the authority is out of order.
        return(user == owner || Authorized[user]);
        //return (address(auth) != address(0) && auth.canCall(user, address(this), functionSig)) || user == owner;
    }

    function setNewAuthority(address newAuthority) public {
        require(msg.sender == owner );
        Authorized[newAuthority] = true; 

    }

    function setAuthority(Authority newAuthority) public virtual {
        // We check if the caller is the owner first because we want to ensure they can
        // always swap out the authority even if it's reverting or using up a lot of gas.
        require(msg.sender == owner || authority.canCall(msg.sender, address(this), msg.sig));

        authority = newAuthority;

        emit AuthorityUpdated(msg.sender, newAuthority);
    }

    function setOwner(address newOwner) public virtual requiresAuth {
        owner = newOwner;

        emit OwnerUpdated(msg.sender, newOwner);
    }
}

/// @notice A generic interface for a contract which provides authorization data to an Auth instance.
/// @author Solmate (https://github.com/transmissions11/solmate/blob/main/src/auth/Auth.sol)
/// @author Modified from Dappsys (https://github.com/dapphub/ds-auth/blob/master/src/auth.sol)
interface Authority {
    function canCall(
        address user,
        address target,
        bytes4 functionSig
    ) external view returns (bool);
}
