import {FixedPointMathLib} from "lib/solmate/src/utils/FixedPointMathLib.sol";
import "lib/forge-std/src/console.sol";

struct SwapParams{
  uint256 s; 
  uint256 a; 
  uint256 b; 
  bool up;

  uint256 pieceWisePrice;   
}

/*
@notice linear curves followed by flat curve 
      - - - - 
    /
  /
/
@dev all computations are done in WAD space, any conversion if needed should be made in the parent contract
*/
library LinearPiecewiseCurve{
    uint256 public constant PRECISION = 1e18; 
    using FixedPointMathLib for uint256; 

    function outGivenArea(
        uint256 amount, 
        SwapParams memory vars
        ) internal view returns(uint256 amountDelta, uint256 resultPrice){
        console.log('varsb', vars.b, amount, vars.a); 
        if(vars.a==0) return (amount.divWadDown(vars.b), vars.b);  

        uint256 curPrice = vars.a.mulWadDown(vars.s) + vars.b; 

        amountDelta = amount > 0 ? (
            (
                (curPrice.mulWadDown(curPrice) + 2*(vars.a.mulWadDown(amount))) * PRECISION

            ).sqrt() - curPrice
        ).divWadDown(vars.a) : 0; 

        resultPrice = vars.a.mulWadDown(amountDelta + vars.s) + vars.b; 
    }

    function outGivenSupply(
        uint256 amount,
        uint256 s, 
        uint256 a, 
        uint256 b
        ) internal pure returns(uint256 amountDelta, uint256 resultPrice){
        uint256 z = b + a.mulWadDown(s) - a.mulWadDown(amount)/2;  
        amountDelta = amount.mulWadDown(z); 
        resultPrice = a.mulWadDown(s-amount) + b; 
    }

    /// @param amount: amount of base in if up, trade in if down 
    /// returns amountDelta wanted token returned 
    function amountOutGivenIn( 
        uint256 amount,
        SwapParams memory vars
        ) 
        public 
        view 
        returns(uint256 amountDelta, uint256 resultPrice) {

        if(vars.pieceWisePrice ==0){
            if (vars.up) (amountDelta, resultPrice) = outGivenArea(amount, vars); 
            else (amountDelta, resultPrice) = outGivenSupply(amount, vars.s, vars.a, vars.b); 
        } else{     

            uint256 pieceWisePoint; 

            if(vars.up){
                console.log('wtf', amount, vars.b); 
                    console.log('where', amount.divWadDown(vars.b)); 

                if(vars.a==0) return (amount.divWadDown(vars.b), vars.b);  
                console.log('here', (vars.pieceWisePrice-vars.b), vars.a); 

                pieceWisePoint = (vars.pieceWisePrice-vars.b).divWadDown(vars.a); 
                // get maximum area till piecewiseprice
                // if amount is larger, then cap it to maximum area 
                // amount-maximum area will be used as remainder 

                // Haven't cross to second curve yet 
                if(pieceWisePoint> vars.s){
                    uint256 maximumArea = areaUnderCurve(pieceWisePoint-vars.s, vars.s,vars.a,vars.b  ); 
                    (amountDelta, resultPrice) = maximumArea >= amount
                        ? outGivenArea(amount, vars)
                        : (pieceWisePoint - vars.s + (amount - maximumArea).divWadDown(vars.pieceWisePrice), vars.pieceWisePrice);
                } else{

                    pieceWisePoint = (vars.pieceWisePrice-vars.b).divWadDown(vars.a); 
                    amountDelta = amount.divWadDown(vars.pieceWisePrice); 
                    resultPrice = vars.pieceWisePrice; 
                }
            } else{
                if(pieceWisePoint> vars.s){
                    (amountDelta, resultPrice) = outGivenSupply(amount, vars.s, vars.a, vars.b); 
                } else{ 

                    uint256 maximumArea = (vars.s - pieceWisePoint).divWadDown(vars.pieceWisePrice); 

                    (amountDelta, resultPrice) = maximumArea >= amount
                        ? (amount.mulWadDown(vars.pieceWisePrice), vars.pieceWisePrice)
                        : ((maximumArea).mulWadDown(vars.pieceWisePrice) 
                        + areaUnderCurveDown(amount-maximumArea, pieceWisePoint, vars.a, vars.b)
                        , vars.a.mulWadDown(vars.s-amount) + vars.b); 
                }
            }
        }
    }

    // @notice amount is in supply 
    function areaUnderCurveDown(
        uint256 amount,
        uint256 s, 
        uint256 a, 
        uint256 b
        ) public pure returns(uint256 area){
        uint256 z = b + a.mulWadDown(s) - a.mulWadDown(amount)/2;  
        area = amount.mulWadDown(z); 
    }

    /// @notice calculates area under the curve from s to s+amount
    /// result = a * amount / 2  * (2* supply + amount) + b * amount
    /// returned in collateral decimals
    function areaUnderCurve(
        uint256 amount, 
        uint256 s, 
        uint256 a, 
        uint256 b) 
        public
        pure 
        returns(uint256 area){
        area = ( a.mulWadDown(amount) / 2 ).mulWadDown(2 * s + amount) + b.mulWadDown(amount); 
    }

    function min(uint256 a, uint256 b) public pure returns (uint256) {
        return a <= b ? a : b;
    }

    function xMax(uint256 curPrice, uint256 b, uint256 a) public pure returns(uint256){
        if(a==0) return type(uint256).max; 
        return (curPrice-b).divWadDown(a); 
    }

}








","keccak256":"0x2792397b6bf328c1dee02b9c0cf295c043ad6af90a79e9b54c88732c69512436"},"contracts/chainlink/VRFConsumerBaseV2.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.4;

/** ****************************************************************************
 * @notice Interface for contracts using VRF randomness
 * *****************************************************************************
 * @dev PURPOSE
 *
 * @dev Reggie the Random Oracle (not his real job) wants to provide randomness
 * @dev to Vera the verifier in such a way that Vera can be sure he's not
 * @dev making his output up to suit himself. Reggie provides Vera a public key
 * @dev to which he knows the secret key. Each time Vera provides a seed to
 * @dev Reggie, he gives back a value which is computed completely
 * @dev deterministically from the seed and the secret key.
 *
 * @dev Reggie provides a proof by which Vera can verify that the output was
 * @dev correctly computed once Reggie tells it to her, but without that proof,
 * @dev the output is indistinguishable to her from a uniform random sample
 * @dev from the output space.
 *
 * @dev The purpose of this contract is to make it easy for unrelated contracts
 * @dev to talk to Vera the verifier about the work Reggie is doing, to provide
 * @dev simple access to a verifiable source of randomness. It ensures 2 things:
 * @dev 1. The fulfillment came from the VRFCoordinator
 * @dev 2. The consumer contract implements fulfillRandomWords.
 * *****************************************************************************
 * @dev USAGE
 *
 * @dev Calling contracts must inherit from VRFConsumerBase, and can
 * @dev initialize VRFConsumerBase's attributes in their constructor as
 * @dev shown:
 *
 * @dev   contract VRFConsumer {
 * @dev     constructor(<other arguments>, address _vrfCoordinator, address _link)
 * @dev       VRFConsumerBase(_vrfCoordinator) public {
 * @dev         <initialization with other arguments goes here>
 * @dev       }
 * @dev   }
 *
 * @dev The oracle will have given you an ID for the VRF keypair they have
 * @dev committed to (let's call it keyHash). Create subscription, fund it
 * @dev and your consumer contract as a consumer of it (see VRFCoordinatorInterface
 * @dev subscription management functions).
 * @dev Call requestRandomWords(keyHash, subId, minimumRequestConfirmations,
 * @dev callbackGasLimit, numWords),
 * @dev see (VRFCoordinatorInterface for a description of the arguments).
 *
 * @dev Once the VRFCoordinator has received and validated the oracle's response
 * @dev to your request, it will call your contract's fulfillRandomWords method.
 *
 * @dev The randomness argument to fulfillRandomWords is a set of random words
 * @dev generated from your requestId and the blockHash of the request.
 *
 * @dev If your contract could have concurrent requests open, you can use the
 * @dev requestId returned from requestRandomWords to track which response is associated
 * @dev with which randomness request.
 * @dev See "SECURITY CONSIDERATIONS" for principles to keep in mind,
 * @dev if your contract could have multiple requests in flight simultaneously.
 *
 * @dev Colliding `requestId`s are cryptographically impossible as long as seeds
 * @dev differ.
 *
 * *****************************************************************************
 * @dev SECURITY CONSIDERATIONS
 *
 * @dev A method with the ability to call your fulfillRandomness method directly
 * @dev could spoof a VRF response with any random value, so it's critical that
 * @dev it cannot be directly called by anything other than this base contract
 * @dev (specifically, by the VRFConsumerBase.rawFulfillRandomness method).
 *
 * @dev For your users to trust that your contract's random behavior is free
 * @dev from malicious interference, it's best if you can write it so that all
 * @dev behaviors implied by a VRF response are executed *during* your
 * @dev fulfillRandomness method. If your contract must store the response (or
 * @dev anything derived from it) and use it later, you must ensure that any
 * @dev user-significant behavior which depends on that stored value cannot be
 * @dev manipulated by a subsequent VRF request.
 *
 * @dev Similarly, both miners and the VRF oracle itself have some influence
 * @dev over the order in which VRF responses appear on the blockchain, so if
 * @dev your contract could have multiple VRF requests in flight simultaneously,
 * @dev you must ensure that the order in which the VRF responses arrive cannot
 * @dev be used to manipulate your contract's user-significant behavior.
 *
 * @dev Since the block hash of the block which contains the requestRandomness
 * @dev call is mixed into the input to the VRF *last*, a sufficiently powerful
 * @dev miner could, in principle, fork the blockchain to evict the block
 * @dev containing the request, forcing the request to be included in a
 * @dev different block with a different hash, and therefore a different input
 * @dev to the VRF. However, such an attack would incur a substantial economic
 * @dev cost. This cost scales with the number of blocks the VRF oracle waits
 * @dev until it calls responds to a request. It is for this reason that
 * @dev that you can signal to an oracle you'd like them to wait longer before
 * @dev responding to the request (however this is not enforced in the contract
 * @dev and so remains effective only in the case of unmodified oracle software).
 */
abstract contract VRFConsumerBaseV2 {
  error OnlyCoordinatorCanFulfill(address have, address want);
  address private immutable vrfCoordinator;

  /**
   * @param _vrfCoordinator address of VRFCoordinator contract
   */
  constructor(address _vrfCoordinator) {
    vrfCoordinator = _vrfCoordinator;
  }

  /**
   * @notice fulfillRandomness handles the VRF response. Your contract must
   * @notice implement it. See "SECURITY CONSIDERATIONS" above for important
   * @notice principles to keep in mind when implementing your fulfillRandomness
   * @notice method.
   *
   * @dev VRFConsumerBaseV2 expects its subcontracts to have a method with this
   * @dev signature, and will call it once it has verified the proof
   * @dev associated with the randomness. (It is triggered via a call to
   * @dev rawFulfillRandomness, below.)
   *
   * @param requestId The Id initially returned by requestRandomness
   * @param randomWords the VRF output expanded to the requested number of words
   */
  function fulfillRandomWords(uint256 requestId, uint256[] memory randomWords) internal virtual;

  // rawFulfillRandomness is called by VRFCoordinator when it receives a valid VRF
  // proof. rawFulfillRandomness then calls fulfillRandomness, after validating
  // the origin of the call
  function rawFulfillRandomWords(uint256 requestId, uint256[] memory randomWords) external {
    if (msg.sender != vrfCoordinator) {
      revert OnlyCoordinatorCanFulfill(msg.sender, vrfCoordinator);
    }
    fulfillRandomWords(requestId, randomWords);
  }
}","keccak256":"0xd4697556c95e0f2e5055eea0b07b4d476887052d95dbf838b3cf7c32436c71d1","license":"MIT"},"contracts/chainlink/VRFCoordinatorV2Interface.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

interface VRFCoordinatorV2Interface {
  /**
   * @notice Get configuration relevant for making requests
   * @return minimumRequestConfirmations global min for request confirmations
   * @return maxGasLimit global max for request gas limit
   * @return s_provingKeyHashes list of registered key hashes
   */
  function getRequestConfig()
    external
    view
    returns (
      uint16,
      uint32,
      bytes32[] memory
    );

  /**
   * @notice Request a set of random words.
   * @param keyHash - Corresponds to a particular oracle job which uses
   * that key for generating the VRF proof. Different keyHash's have different gas price
   * ceilings, so you can select a specific one to bound your maximum per request cost.
   * @param subId  - The ID of the VRF subscription. Must be funded
   * with the minimum subscription balance required for the selected keyHash.
   * @param minimumRequestConfirmations - How many blocks you'd like the
   * oracle to wait before responding to the request. See SECURITY CONSIDERATIONS
   * for why you may want to request more. The acceptable range is
   * [minimumRequestBlockConfirmations, 200].
   * @param callbackGasLimit - How much gas you'd like to receive in your
   * fulfillRandomWords callback. Note that gasleft() inside fulfillRandomWords
   * may be slightly less than this amount because of gas used calling the function
   * (argument decoding etc.), so you may need to request slightly more than you expect
   * to have inside fulfillRandomWords. The acceptable range is
   * [0, maxGasLimit]
   * @param numWords - The number of uint256 random values you'd like to receive
   * in your fulfillRandomWords callback. Note these numbers are expanded in a
   * secure way by the VRFCoordinator from a single random value supplied by the oracle.
   * @return requestId - A unique identifier of the request. Can be used to match
   * a request to a response in fulfillRandomWords.
   */
  function requestRandomWords(
    bytes32 keyHash,
    uint64 subId,
    uint16 minimumRequestConfirmations,
    uint32 callbackGasLimit,
    uint32 numWords
  ) external returns (uint256 requestId);

  /**
   * @notice Create a VRF subscription.
   * @return subId - A unique subscription id.
   * @dev You can manage the consumer set dynamically with addConsumer/removeConsumer.
   * @dev Note to fund the subscription, use transferAndCall. For example
   * @dev  LINKTOKEN.transferAndCall(
   * @dev    address(COORDINATOR),
   * @dev    amount,
   * @dev    abi.encode(subId));
   */
  function createSubscription() external returns (uint64 subId);

  /**
   * @notice Get a VRF subscription.
   * @param subId - ID of the subscription
   * @return balance - LINK balance of the subscription in juels.
   * @return reqCount - number of requests for this subscription, determines fee tier.
   * @return owner - owner of the subscription.
   * @return consumers - list of consumer address which are able to use this subscription.
   */
  function getSubscription(uint64 subId)
    external
    view
    returns (
      uint96 balance,
      uint64 reqCount,
      address owner,
      address[] memory consumers
    );

  /**
   * @notice Request subscription owner transfer.
   * @param subId - ID of the subscription
   * @param newOwner - proposed new owner of the subscription
   */
  function requestSubscriptionOwnerTransfer(uint64 subId, address newOwner) external;

  /**
   * @notice Request subscription owner transfer.
   * @param subId - ID of the subscription
   * @dev will revert if original owner of subId has
   * not requested that msg.sender become the new owner.
   */
  function acceptSubscriptionOwnerTransfer(uint64 subId) external;

  /**
   * @notice Add a consumer to a VRF subscription.
   * @param subId - ID of the subscription
   * @param consumer - New consumer which can use the subscription
   */
  function addConsumer(uint64 subId, address consumer) external;

  /**
   * @notice Remove a consumer from a VRF subscription.
   * @param subId - ID of the subscription
   * @param consumer - Consumer to remove from the subscription
   */
  function removeConsumer(uint64 subId, address consumer) external;

  /**
   * @notice Cancel a subscription
   * @param subId - ID of the subscription
   * @param to - Where to send the remaining LINK to
   */
  function cancelSubscription(uint64 subId, address to) external;

  /*
   * @notice Check to see if there exists a request commitment consumers
   * for all consumers and keyhashes for a given sub.
   * @param subId - ID of the subscription
   * @return true if there exists at least one unfulfilled request for the subscription, false
   * otherwise.
   */
  function pendingRequestExists(uint64 subId) external view returns (bool);
}","keccak256":"0xadc1f1babaa7202145a22d82c85176cbe7f1fae993f2c11dabdf6058aebea6b2","license":"MIT"},"contracts/global/GlobalStorage.sol":{"content":"pragma solidity ^0.8.16;
import "./types.sol"; 
import {PerpTranchePricer} from "../libraries/pricerLib.sol"; 
import {CollateralPricer} from "../libraries/lendingOracleLib.sol"; 

import {Instrument} from "../vaults/instrument.sol"; 
import {FixedPointMathLib} from "lib/solmate/src/utils/FixedPointMathLib.sol";

import "lib/forge-std/src/console.sol";

contract StorageHandler{
	using PerpTranchePricer for PricingInfo;
	using CollateralPricer for PoolPricingParam; 
    using FixedPointMathLib for uint256;

	uint256 constant BASE_UNIT = 1e18; 

    modifier onlyProtocol() {

        _;
    }

  	constructor() 
  		{
    // controller = Controller(_controllerAddress);
    // push empty market
    	markets.push(makeEmptyMarketData());

    // owner = msg.sender; 
  	}


	/// @notice called at market creation 
	function setNewInstrument(
		uint256 marketId, 
		uint256 initialPrice, 
		uint256 multiplier, 
		bool constantRF, 
		InstrumentData memory idata, 
		CoreMarketData memory mdata) external onlyProtocol {
		PricingInfos[marketId].setNewPrices(initialPrice, multiplier, marketId, constantRF); 

		storeNewProposal(marketId, idata); 
	}


    //--- Perp Pricing ---// 
	mapping(uint256=> PricingInfo) public PricingInfos; 

	function getPricingInfo(uint256 marketId) public view returns(PricingInfo memory){
		return PricingInfos[marketId]; 
	}

	function updatePricingInfo(uint256 marketId, PricingInfo memory newInfo) external onlyProtocol{
		PricingInfos[marketId] = newInfo; 
	}

	function setRF(uint256 marketId, bool isConstant) external onlyProtocol {
		PricingInfos[marketId].setRF(isConstant); 
	}

	/// @notice updates whenever uRate changes 
	function refreshPricing(uint256 marketId, uint256 uRate) public onlyProtocol{
		PricingInfos[marketId].storeNewPSU(uRate); 
	}

	function viewCurrentPricing(uint256 marketId) public view returns(uint256, uint256, uint256) {
		InstrumentData memory data = InstrumentDatas[marketId]; 
		return (PricingInfos[marketId].viewCurrentPricing(
			data.instrument_address, 
			data.poolData, 
			markets[marketId].longZCB.totalSupply()
		));  
	}

	function checkIsSolventConstantRF(uint256 marketId) public view returns(bool){
		InstrumentData memory data = InstrumentDatas[marketId]; 
		uint256 psu = PerpTranchePricer.constantRF_PSU(
			data.poolData.inceptionPrice, 
			data.poolData.promisedReturn, 
			data.poolData.inceptionTime); 
		return PerpTranchePricer.isSolvent(data.instrument_address, psu, markets[marketId].longZCB.totalSupply(), 
			data.poolData); 
	}


	//--- Instrument ---//
	uint256 public lastTradedRate; 
	uint256 public lastTradedTime; 
	uint256 public constant TIME_WINDOW = 10e18; // for rate oracle 
	mapping(uint256=> PoolPricingParam) PoolPricingParams; 

	mapping(uint256=> ExchangeRateData) storedRates; //marketId-> last exchange rate

	mapping(uint256=> InstrumentData) public InstrumentDatas; 

	function getInstrumentData(uint256 marketId) public view returns(InstrumentData memory){
		return InstrumentDatas[marketId]; 
	}

	function getInstrumentAddress(uint256 marketId) public view returns(address){
		return InstrumentDatas[marketId].instrument_address; 
	}

	function storeNewProposal(uint256 marketId, InstrumentData memory data) public onlyProtocol{
		InstrumentDatas[marketId] = data; 
	}

	/// @notice store instrument exchange rate to get delayed rates 
	/// called whenever instrument exchange rate changes
	function storeExchangeRateOracle(uint256 marketId, uint256 newRate) public onlyProtocol returns(uint256){
		ExchangeRateData storage rateData = storedRates[marketId];

		rateData.lastOracleRate = rateData.initialized? queryExchangeRateOracle(marketId) : newRate; 
		rateData.lastRate = newRate; 
		rateData.lastTime = block.timestamp; 
		rateData.initialized = true; 

		return rateData.lastOracleRate; 
	}

	function queryExchangeRateOracle(uint256 marketId) public view returns(uint256){
		ExchangeRateData memory rateData = storedRates[marketId];
		uint256 timeDiff = (block.timestamp -rateData.lastTime) * BASE_UNIT; 
		uint256 timeDivWindow = timeDiff.divWadDown(TIME_WINDOW); 

		// Liear interpolation of last rate and last oracle rate
		rateData.lastOracleRate = TIME_WINDOW >= timeDiff
			? rateData.lastRate.mulWadDown(min(timeDivWindow, BASE_UNIT)) 
				+ rateData.lastOracleRate.mulWadDown((TIME_WINDOW - timeDiff).divWadDown(TIME_WINDOW))
			: rateData.lastRate;

		return rateData.lastOracleRate; 
	}

	function setPoolPricingParams(uint256 marketId, PoolPricingParam memory params) public onlyProtocol{
		PoolPricingParams[marketId] = params; 
	}

	/// @notice applicable to lending pool instruments only. Needs to be called
	/// whenever utilization rate is about to get updates. i.e while borrowing/
	function refreshAndGetNewLTV(uint256 marketId, uint256 newURate) public onlyProtocol returns(uint256){
		return PoolPricingParams[marketId].storeNewLTV(newURate); 
	}






	//--- Market ---//
  	CoreMarketData[] public markets;

	function storeNewMarket(CoreMarketData memory data) public onlyProtocol  returns(uint256 marketId){
		// MarketDatas[marketId] = data; 
		marketId = markets.length; 
		markets.push(data); 
		// uint256 base_budget = 1000 * BASE_UNIT; //TODO 
		// setMarketPhase(marketId, true, true, base_budget);
	}

	function getMarket(uint256 marketId) public view returns(CoreMarketData memory data){
		return markets[marketId]; 
	}

	function getMarketLength() public view returns(uint256){
		return markets.length; 
	}

	function makeEmptyMarketData() internal pure returns (CoreMarketData memory) {
		return CoreMarketData(
		    SyntheticZCBPool(address(0)),
		    ERC20(address(0)),
		    ERC20(address(0)),
		    "",
		    0,
		    0, 
		    false
		);
	}    

	function zcbMaxPrice(uint256 marketId) public view returns(uint256){
		return 1e18; 
	} 




    function min(uint256 a, uint256 b) internal pure returns (uint256) {
        return a <= b ? a : b;
    }



}


","keccak256":"0x8e47572805c87a14822630f233593f06afee93d50a7535d95337b1e7f9ece7fc"},"contracts/global/types.sol":{"content":"pragma solidity ^0.8.16;

import {SyntheticZCBPool} from "../bonds/bondPool.sol"; 
import {ERC20} from "lib/solmate/src/tokens/ERC20.sol";

library Constants{
	uint256 public constant THRESHOLD_PJU = 5e17; // can't buy if below 0.5 
	uint256 public constant MAX_VAULT_URATE = 10e17; 

}



struct MarketData {
    address instrument_address;
    address utilizer;
}

struct ApprovalData {
    uint256 managers_stake;
    uint256 approved_principal;
    uint256 approved_yield;
}

struct InstrumentData {
	bytes32 name;
	bool isPool; 
	// Used to determine if the Vault will operate on a Instrument.
	bool trusted;
	// Balance of the contract denominated in Underlying, 
	// used to determine profit and loss during harvests of the Instrument.  
	// represents the amount of debt the Instrument has incurred from this vault   
	uint256 balance; // in underlying, IMPORTANT to get this number right as it modifies key states 
	uint256 faceValue; // in underlying
	uint256 marketId;

	uint256 principal; //this is total available allowance in underlying
	uint256 expectedYield; // total interest paid over duration in underlying
	uint256 duration;
	string description;
	address instrument_address;
	InstrumentType instrument_type;
	uint256 maturityDate;
	PoolData poolData; 
}

/// @notice probably should have default parameters for each vault
struct PoolData{
	uint256 saleAmount; 
	uint256 initPrice; // init price of longZCB in the amm 
	uint256 promisedReturn; //per unit time 
	uint256 inceptionTime;
	uint256 inceptionPrice; // init price of longZCB after assessment 
	uint256 leverageFactor; // leverageFactor * manager collateral = capital from vault to instrument
	uint256 managementFee; // sum of discounts for high reputation managers/validators
}

struct PoolPricingParam{
	uint256 incrementRate; 
	uint256 urateUpper; 
	uint256 urateLower;
	uint256 prevAccrueTime; 
	uint256 prevURate; 

	uint256 maxBorrowable; 
}	

struct ExchangeRateData{
	uint256 lastRate; 
	uint256 lastTime; 
	uint256 lastOracleRate;
	bool initialized;  
}

struct PricingInfo{
	uint256 psu; 

	uint256 prevAccrueTime; 
	uint256 prevIntervalRp; //per second compounding promised return, function of urate 

	// Constants for a given market 
	uint256 URATE_MULTIPLIER; 
	uint256 ID; 

	bool constantRF; 
}


struct CoreMarketData {
	SyntheticZCBPool bondPool; 
	ERC20 longZCB;
	ERC20 shortZCB; 
	string description; // instrument description
	uint256 creationTimestamp;
	uint256 resolutionTimestamp;
	bool isPool; 
}

struct MarketPhaseData {
	bool duringAssessment;
	bool onlyReputable;
	bool resolved;
	bool alive;
	bool atLoss;
	// uint256 min_rep_score;
	uint256 base_budget;
}

  /// @param N: upper bound on number of validators chosen.
  /// @param sigma: validators' stake
  /// @param alpha: minimum managers' stake
  /// @param omega: high reputation's stake 
  /// @param delta: Upper and lower bound for price which is added/subtracted from alpha 
  /// @param r: reputation percentile for reputation constraint phase
  /// @param s: senior coefficient; how much senior capital the managers can attract at approval 
  /// @param steak: steak*approved_principal is the staking amount.
  /// param beta: how much volatility managers are absorbing 
  /// param leverage: how much leverage managers can apply 
  /// param base_budget: higher base_budget means lower decentralization, 
  /// @dev omega always <= alpha
 struct MarketParameters{
	uint256 N;
	uint256 sigma; 
	uint256 alpha; 
	uint256 omega;
	uint256 delta; 
	uint256 r;
	uint256 s;
	uint256 steak;
  }



struct ResolveVar{
	uint256 endBlock; 
	bool isPrepared; 
}

struct Order{
	bool isLong; 
	uint256 price; 
	uint256 amount; 
	uint256 orderId; 
	address owner; 
}




enum InstrumentType {
    CreditLine,
    CoveredCallShort,
    LendingPool, 
    StraddleBuy,
    LiquidityProvision, 
    Other
}
